var halfRoundedRect = function(x, y, w, h, r){
    pushMatrix();
    translate(x, y);
    if(h < 0){
        scale(1, -1);
        h = abs(h);
    }
    pushStyle();
    strokeCap(SQUARE);
    line(-w/2, 0, -w/2, h - r);
    arc(-w/2 + r, h - r, r * 2, r * 2, 90, 180);
    line(-w/2 + r, h, w/2 - r, h);
    arc(w/2 - r, h - r, r * 2, r * 2, 0, 90);
    line(w/2, 0, w/2, h - r);
    popStyle();
    pushStyle();
    noStroke();
    rectMode(CORNER);
    rect(-w/2 - 1, -1, w + 1, h - r + 2);
    rect(-w/2 + r - 1, h - r, w - r * 2 + 1, r);
    popStyle();
    popMatrix();
};

var squarians = [];

var Squarian = function(constructor){
    var defaults = {
        x: width,
        y: -4,
        targetX: width,
        targetY: -4,
        targetXFactor: 0.1,
        targetYFactor: 0.1,
        direction: 0,
        faceFactor: 0,
        blinking: false,
        blinkFactor: 1,
        blinkSpeed: 0.11,
        isWalking: false,
        isTalking: false,
        isWhistling: false,
        isSleeping: false,
    };
    
    for(var key in defaults){
        this[key] = defaults[key];
    }
    for(var key in constructor){
        this[key] = constructor[key];
    }
    
    squarians.push(this);
};

Squarian.prototype.blink = function(){
    this.blinkFactor = 0;
};
Squarian.prototype.sleep = function(){
    this.isSleeping = true;
    this.blink();
};
Squarian.prototype.draw = function(secondaryColor){
    this.x += (this.targetX - this.x) * this.targetXFactor;
    this.y += (this.targetY - this.y) * this.targetYFactor;
    this.faceFactor += (this.direction - this.faceFactor) * 0.5;
    
    if(round(random(0, 50)) === 0 && !this.isSleeping){
        this.blink();
    }
    this.blinkFactor = min(this.blinkFactor + this.blinkSpeed, 1);
    if(this.isSleeping){
        this.blinkFactor = min(this.blinkFactor + this.blinkSpeed, 0.35);
    }
    
    
    pushMatrix();
    translate(this.x, this.y - 30 + (sin(frameCount * 20) - 1) * this.isWalking);
    
    Fill();
    noStroke();
    rectMode(CENTER);
    rect(0, 0, 50, 60, 15);
    fill(secondaryColor);
    //fill(colorWithAlpha(secondaryColor, this.isSleeping ? 150 : 255));
    //Eyes
    rect(-6 + this.faceFactor * 8, -6, 6, (1 - sin(this.blinkFactor * 180)) * 20, 15);
    rect(6 + this.faceFactor * 8, -6, 6, (1 - sin(this.blinkFactor * 180)) * 17, 15);
    //Mouth
    fill(secondaryColor);
    var n = noise(0, frameCount * 0.04);
    if(this.isWhistling){
        ellipse(2 + this.faceFactor * 4, 16, n * 23 - 5, n * 23 - 5);
    }else{
        rect(2 + this.faceFactor * 4, 15 + n * 10 * this.isTalking, 13, 3, 15);
    }
    
    //Whistling/Sleeping
    if(this.isWhistling || this.isSleeping){
        if(this.direction === -1){
            scale(-1, 1);
        }
        for(var p = 0; p < 10; p++){
            var f = (frameCount * (this.isWhistling ? 0.007 : 0.003) + p * 0.1) % 1;
            pushMatrix();
            translate(-40 - f * 400, -35 + sin(p * 165) * 10 - f * 80);
            rotate(sin(p * 65) * 10);
            scale(3 * pow(0.05, f));
            if(this.direction === -1){
                scale(-1, 1);
            }
            
            Fill(255 * (1 - f) * min(frameCount * 0.005, 1));
            text(this.isWhistling ? ["♩", "♪", "♫"][p % 3] : "z", 0, 0);
            popMatrix();
        }
    }
    
    if(this.accessories){
        this.accessories();
    }
    
    rectMode(CENTER);
    
    popMatrix();
};

