//Chair•E
var chairE = function(timer, facingRight){
    pushMatrix();
    translate(larry.x, sin(timer * 40) * 1);
    if(!facingRight){
        scale(-1, 1);
    }
    
    noFill();
    strokeWeight(14);
    strokeJoin(ROUND);
    beginShape();
    vertex(-35, -55);
    vertex(-35, -15);
    vertex(35, -15);
    endShape();
    
    textSize(10);
    for(var p = 0; p < 10; p++){
        var f = (timer * 0.007 + p * 0.1) % 1;
        pushMatrix();
        translate(-65 - f * 400, -35 + sin(p * 165) * 10);
        rotate(sin(p * 65) * 10);
        scale(3 * pow(0.05, f));
        scale(0.8, 1);
        if(!facingRight){
            scale(-1, 1);
        }
        
        Fill(255 * (1 - f) * min(timer * 0.005, 1));
        text("PUTT", 0, 0);
        popMatrix();
    }
    
    popMatrix();
    
    larry.targetY = -25 + sin(timer * 10) * 2;
};

