for(var s = 0; s < script.length; s++){
    var scene = script[s];
    var previousScene = script[s - 1];
    scene.index = s;
    scene.timer = 0;
    if(previousScene){
        if(!scene.background){
            scene.background = previousScene.background;
        }
        if(scene.topText === undefined){
            scene.topText = previousScene.topText;
        }
        if(scene.bottomText === undefined){
            scene.bottomText = previousScene.bottomText;
        }
    }
}

var mouseIsOver;
mouseOver = function(){ mouseIsOver = true; };
mouseOut = function(){ mouseIsOver = false; };

mouseClicked = function(){
    currentSceneNumber++;
    if(mouseButton === RIGHT){
        currentSceneNumber = 11;
    }
    
    script[currentSceneNumber].init();
};

keyPressed = function(){
    if(currentSceneNumber === 0){
        switch(keyCode){
            case LEFT:
                larry.direction = -1;
                break;
            case DOWN:
                larry.direction = 0;
                break;
            case RIGHT:
                larry.direction = 1;
                break;
        }
    }
};

//script[currentSceneNumber].init();

draw = function() {
    var scene = script[currentSceneNumber];
    var previousScene = script[currentSceneNumber - 1];
    
    var backgroundColor = lerpColor(previousScene.background, scene.background, min(scene.timer / 70, 1));
    background(backgroundColor);
    
    Fill(scene.timer);
    Stroke();
    
    //Scene
    pushMatrix();
        //Draw everything relative to the center of the ground
        translate(halfWidth, groundY);
        
        //Ground
        strokeWeight(4);
        line(-3 - halfWidth, 0, halfWidth - width * pow(0.9, script[2].timer - 35), 0);
        
        //Scene
        pushMatrix();
            scene.draw(backgroundColor);
        popMatrix();
        
        //Draw all squarians
        for(var s= 0; s < squarians.length; s++){
            squarians[s].draw(backgroundColor);
        }
    popMatrix();
    
    //Text
    Fill();
    textSize(17);
    textAlign(CENTER, CENTER);
    if(scene.topText){
        text(scene.topText, halfWidth, 60 - 120 * pow(0.9, scene.timer) * (scene.topText !== previousScene.topText));
    }
    if(scene.bottomText){
        text(scene.bottomText, halfWidth, height - 60 + 120 * pow(0.9, scene.timer) * (scene.bottomText !== previousScene.bottomText));
    }
    if(previousScene.topText && scene.topText !== previousScene.topText){
        text(previousScene.topText, halfWidth, -60 + 120 * pow(0.9, scene.timer));
    }
    if(previousScene.bottomText && scene.bottomText !== previousScene.bottomText){
        text(previousScene.bottomText, halfWidth, height + 60 - 120 * pow(0.9, scene.timer));
    }
    
    for(var s = 0; s <= currentSceneNumber; s++){
        script[s].timer++;
    }
    stroke(255, 0, 0);
};

